DROP TRIGGER IF EXISTS `trig_count`;
DELIMITER //
CREATE TRIGGER `trig_count` AFTER INSERT ON `news_commentary`
 FOR EACH ROW begin
DECLARE updatecount INT;
  set updatecount = ( select count(*) from news_commentary where post_id = new.post_id );

update news_posts set post_commentary_count = updatecount where id = new.post_id;
  
end
//
DELIMITER ;

